************************************************** Sample from page 170 listing 7-1 * of assembly language programming book.* lwasm -o t.bin coco/asmtests/testing.asm && writecocofile -a EDTASM2.DSK testing.asm && writecocofile EDTASM2.DSK t.bin* lwasm -o t.bin testing.asm && writecocofile asmtests.dsk t.bin*************************************************	org	$e00startclrprn	ldx	#$400	* point to 32 width screen	lda	#$ff	* char to displaycl1	sta	,x+	* put byte on screen	cmpx	#$5ff	* end of screen?	bls	cl1	* if not do again	ldx	#$400	* display pointer, mid of the screen	ldy	#cltextcl2	lda	,y+	cmpa	#$04	* end of text?	beq	cl4	* if so go to end	cmpa	#$40	* good video code?	bhs	cl3	* if so use it	adda	#$40	* change to vid codecl3	sta	,x+	* put on screen	bra	cl2	* do it againcl4	rts		* return to basic*************************************************cltext	fcc	/TESTING LINE 0123456789 !"#$/	fcb	$04*************************************************	end	start